mapper.select:
SELECT ID as ID, DB_INSTANCE_ID as DB_INSTANCE_ID, EXECUTE_START_TIME as EXECUTE_START_TIME, SQL_PARAM as SQL_PARAM, CLIENT_IP as CLIENT_IP, DB_NAME as DB_NANME, QUERY_TIMES as QUERY_TIMES, LOCK_TIMES as LOCK_TIMES, PARSER_ROW_COUNTS as PARSER_ROW_COUNTS, RETURN_ROW_COUNTS as RETURN_ROW_COUNTS FROM mas_rds_slow_log
where DB_INSTANCE_ID= #{rdsHosts}
order by QUERY_TIMES desc limit #{startpages},#{pageSize}
